import React from "react";

const IMG_API = "https://image.tmdb.org/t/p/w1280";

const setvoteclass = (vote) => {
    if (vote >= 8) {
        return "green";
    } else if (vote >= 6) {
        return "orange";
    } else {
        return "red";
    }
};

const Movie = ({ title, poster_path, overview,
    vote_average }) => (
    <div className="movie">
        <img
            src={
                poster_path
                    ? IMG_API + poster_path
                    : "https://images.unsplash.com/photo-1485846234645-a62644f84728?ixlib=rb-1.2.1&ixid3DeyJhcHBfaWQiojEyMDd9&auto=format&fit=crop&w3=1480&q80"
            }
            alt={title}
        />

        <div className="movie-info">
            <h3>{title}</h3>
            <span className={
                'tag ${setVoteClass (vote_average)}'
            }>{vote_average}
            </span>
        </div>
        <div className="movie-over">
            <h2>Overview:</h2>
            <p>{overview}</p>
        </div>
    </div>
);
export default Movie;
